  <script>
    document.getElementById('template-contactform').addEventListener('submit', function(event) {
      event.preventDefault(); // Evita el envío del formulario

      // Obtiene los valores de los campos del formulario
      const nombre = document.getElementById('template-contactform-name').value.trim();
      const email = document.getElementById('template-contactform-email').value.trim();
      const asunto = document.getElementById('template-contactform-subject').value.trim();
      const mensaje = document.getElementById('template-contactform-message').value.trim();
      const checkbox = document.getElementById('template-contactform-checkbox').checked;

      // Inicializa una variable para seguir el estado de la validación
      let valid = true;

      // Validación del campo de nombre
      if (nombre.length < 3) {
        valid = false;
        document.getElementById('error-name').style.display = 'inline';
      } else {
        document.getElementById('error-name').style.display = 'none';
      }

      // Validación del campo de correo
      if (email === '') {
        valid = false;
        document.getElementById('error-email').style.display = 'inline';
      } else {
        document.getElementById('error-email').style.display = 'none';
      }

      // Validación del campo de asunto
      if (asunto === '') {
        valid = false;
        document.getElementById('error-subject').style.display = 'inline';
      } else {
        document.getElementById('error-subject').style.display = 'none';
      }

      // Validación del campo de mensaje
      if (mensaje === '') {
        valid = false;
        document.getElementById('error-message').style.display = 'inline';
      } else {
        document.getElementById('error-message').style.display = 'none';
      }

      // Validación de la casilla de verificación
      if (!checkbox) {
        valid = false;
        document.getElementById('error-checkbox').style.display = 'inline';
      } else {
        document.getElementById('error-checkbox').style.display = 'none';
      }

      // Si es válido, envía el formulario
      if (valid) {
        this.submit();
      }
    });
  </script>
		

